// a component in react is just a javascript function
import './ExpenseItem.css'
import ExpenseDate  from './ExpenseDate';
import Card from '../UI/Card';
import {useState} from 'react';

function ExpenseItem(props) {
  // you are only allowed one root element (e.g. h2, div, p) per return statement
  // one workaround is encapsulating everything into one div

  const [title, setTitle] = useState(props.title); // useState always returns an array of two : the current value, and the update method

//  const clickHandler = () => {
//    setTitle('Updated!'); // schedule takes time, console log will not immediately reflect this update
//    console.log(title);
//  } // u can set it as a function too, if triggered upon an event, is conventional to call it a handler

  return (
    <Card className='expense-item'>
      {/* need to change from date format to string */}
      <ExpenseDate expenseDate={props.date}/>
      <div className='expense-item__description'>
        <h2>{props.title}</h2>
        <div className='expense-item__price'>${props.amount}</div>
      </div>
    </Card>
  );
}

export default ExpenseItem;
